mapper.select:
SELECT PATH
  FROM EVENT_IMAGE_T
 WHERE EVENT_NO = #{eventNo}

mapper.update:
UPDATE EVENT_T
   SET HIT = HIT +1
 WHERE EVENT_NO = #{eventNo}

mapper.insert:

  SELECT EVENT_SEQ.NEXTVAL
    FROM DUAL

INSERT INTO EVENT_T (
      EVENT_NO
    , TITLE
    , CONTENTS
    , HIT
    , STATE
    , EVENT_THUMNAIL_URL
    , DISCOUNT_PERCENT
    , DISCOUNT_PRICE
    , START_AT
    , END_AT
    ) VALUES (
      #{eventNo}
    , #{title}
    , #{contents}
    , 0
    , 1
    , #{eventThumnailUrl}
    , #{discountPercent}
    , #{discountPrice}
    , #{startAt}
    , #{endAt}
    )

INSERT INTO EVENT_IMAGE_T (
  EVENT_NO
, PATH
, ORIGINAL_FILENAME
, FILESYSTEM_NAME
) VALUES (
  #{eventNo}
, #{path}
, #{originalFilename}
, #{filesystemName}
)

mapper.update:
UPDATE EVENT_T
   SET STATE = 0
 WHERE EVENT_NO = #{eventNo}

UPDATE EVENT_T
   SET STATE = 1
 WHERE EVENT_NO = #{eventNo}

UPDATE EVENT_T
   SET DISCOUNT_PERCENT = #{discountPercent}
 WHERE EVENT_NO = #{eventNo}

UPDATE EVENT_T
   SET DISCOUNT_PRICE = #{discountPrice}
 WHERE EVENT_NO = #{eventNo}

UPDATE EVENT_T
   SET TITLE = #{title}
    ,  CONTENTS = #{contents}
    ,  HIT = #{hit}
    ,  STATE = #{state}
    ,  EVENT_THUMNAIL_URL = #{eventThumnailUrl}
    ,  DISCOUNT_PERCENT = #{discountPercent}
    ,  DISCOUNT_PRICE = #{discountPrice}
    ,  START_AT = #{startAt}
    ,  END_AT = #{endAt}
 WHERE EVENT_NO = #{eventNo}

UPDATE EVENT_IMAGE_T
   SET PATH = #{path}
     , ORIGINAL_FILENAME = #{originalFilename}
     , FILESYSTEM_NAME = #{filesystemName}
 WHERE EVENT_NO = #{eventNo}
